document.addEventListener("DOMContentLoaded",()=>{document.querySelector(".current-year").textContent=(new Date).getFullYear();const e=`${window.location.origin}/?format=json-pretty`;fetch(e).then(e=>e.json()).then(e=>{const t=e.website.siteTitle;document.querySelector(".site-title").textContent=t}).catch(e=>console.error("Error fetching site title:",e))}),document.addEventListener("DOMContentLoaded",function(){var e=document.querySelectorAll(".page-section");e.forEach(function(e){var t=e.querySelector('div[data-squarehero="section-name"]');if(t){var n=t.getAttribute("sh-section");e.classList.add(n)}})});